import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Asumiendo que estás usando axios para hacer solicitudes HTTP

const editCard = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    // Aquí haces una solicitud a tu backend para obtener los datos de la base de datos
    axios.get('http://localhost:3001/usuarios')
      .then(response => {
        setData(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const handleDelete = (id) => {
    // Aquí implementas la lógica para eliminar un dato de la base de datos
    axios.delete(`/api/datos/${id}`)
      .then(response => {
        // Actualizar la vista después de eliminar el dato
        setData(data.filter(item => item.id !== id));
      })
      .catch(error => {
        console.error('Error deleting data:', error);
      });
  };

  return (
    <div>
      <h1>Tus Datos</h1>
      <table>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Descripción</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {data.map(item => (
            <tr key={item.id}>
              <td>{item.nombre}</td>
              <td>{item.descripcion}</td>
              <td>
                <button onClick={() => handleDelete(item.id)}>Eliminar</button>
                {/* Aquí puedes añadir un botón para editar */}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default editCard;
