import React from 'react';
import App3 from './Vistas/login';
import Dashboard from './Vistas/home';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
function parseJwt (token) {
  const base64Url = token.split('.')[1];
  const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
  const jsonPayload = decodeURIComponent(window.atob(base64).split('').map(function(c) {
      return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
  }).join(''));

  return JSON.parse(jsonPayload);
}

let tokenExistAndStillValid = (parseJwt(localStorage.getItem('token')).exp * 1000 > Date.now());

const PrivateRoute = ({ element: Element, ...rest }) => {
  return tokenExistAndStillValid() ? (
    <Element {...rest} />
  ) : (
    <Navigate to='/login' replace />
  );
};
const Main = () => {
  return (
    <Router>
    <Routes>
      <Route path="/login" element={<App3 />} />
      <Route path="/home" element={<PrivateRoute element={<Dashboard />} />} />
      <Route path="*" element={<Navigate to="/home" />} /> {/* Redirige a la p√°gina de inicio si no se encuentra la ruta */}
    </Routes>
  </Router>

  );
}

export default Main;
