import React from 'react';
import App3 from './Vistas/login';
import Dashboard from './Vistas/home';
import { BrowserRouter as Router, Route, Routes, Navigate,Outlet  } from 'react-router-dom';
function parseJwt (token) {
  const base64Url = token.split('.')[1];
  const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
  const jsonPayload = decodeURIComponent(window.atob(base64).split('').map(function(c) {
      return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
  }).join(''));

  return JSON.parse(jsonPayload);
}

let tokenExistAndStillValid = (parseJwt(localStorage.getItem('token')).exp * 1000 > Date.now());

const ProtectedRoute = ({ children, redirectTo }) => {
  const { isLogged } = tokenExistAndStillValid
  console.log(isLogged)
  if (isLogged) {
    return children || <Outlet />;
  } else {
    return <Navigate to={redirectTo} replace />;
  }
};
const ProtectedRouteLogin = ({ children, redirectTo }) => {
    const { isLogged } = tokenExistAndStillValid;

    if (!isLogged) {
      return children || <Outlet />;
    } else {
      return <Navigate to={redirectTo} replace />;
    }
  };

const Main = () => {
  return (
    <Router>
    <Routes>
    <Route path="/home" element={<ProtectedRoute  redirectTo="/"><Dashboard /></ProtectedRoute>} />
    <Route path="/" element={<ProtectedRouteLogin  redirectTo="/Home"><App3 /></ProtectedRouteLogin>} />

    </Routes>
  </Router>

  );
}

export default Main;
