                    <div class="img">
                       
                    </div>
                </section>
                <section class="informacion">
                    <h4>Sus datos Son:</h4>
                    <p class="datos">EMPLEADO:{nombreCompleto} </p>
                    {tipoAdmin === 1 && Admin === true && (
                     <button>Acci√≥n para administradores</button>
                        )}

                   
                </section>
                
            </div>

            <div class="picture">
                
            </div> 
             
        </div>

        <div class="margin-right"></div>
          
    </main>
    </body>
    );
  }
  
  export default App5;
