import React, { useState } from 'react';

export default function Formulario() {
  // Definir los estados para los campos del formulario
  const [nombre, setNombre] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [url, setUrl] = useState('');
  const [tipo, setTipo] = useState('');
  const [imagen, setImagen] = useState(null);

  // Manejar cambios en los campos del formulario
  const handleNombreChange = (event) => {
    setNombre(event.target.value);
  };

  const handleDescripcionChange = (event) => {
    setDescripcion(event.target.value);
  };

  const handleUrlChange = (event) => {
    setUrl(event.target.value);
  };

  const handleTipoChange = (event) => {
    setTipo(event.target.value);
  };

  const handleImagenChange = (event) => {
    // Obtener la primera imagen del arreglo de archivos seleccionados
    const file = event.target.files[0];
    setImagen(file);
  };

  // Manejar envío del formulario
  const handleSubmit = (event) => {
    event.preventDefault();

    // Aquí puedes hacer lo que desees con los datos del formulario y la imagen
    console.log('Nombre:', nombre);
    console.log('Descripción:', descripcion);
    console.log('URL:', url);
    console.log('Tipo:', tipo);
    console.log('Imagen:', imagen);
  };

  return (
    <div className="p-6">
      <form onSubmit={handleSubmit}>
        <label htmlFor="nombre" className="block text-sm font-medium text-gray-700">
          Nombre
        </label>
        <input type="text" name="nombre" id="nombre" value={nombre} onChange={handleNombreChange} className="mt-1 focus:ring-indigo-500 focus:border-indigo-500 block w-full shadow-sm sm:text-sm border-gray-300 rounded-md" />

        <label htmlFor="descripcion" className="block mt-4 text-sm font-medium text-gray-700">
          Descripción
        </label>
        <textarea name="descripcion" id="descripcion" rows="3" value={descripcion} onChange={handleDescripcionChange} className="mt-1 shadow-sm focus:ring-indigo-500 focus:border-indigo-500 block w-full sm:text-sm border-gray-300 rounded-md"></textarea>

        <label htmlFor="url" className="block mt-4 text-sm font-medium text-gray-700">
          URL
        </label>
        <input type="text" name="url" id="url" value={url} onChange={handleUrlChange} className="mt-1 focus:ring-indigo-500 focus:border-indigo-500 block w-full shadow-sm sm:text-sm border-gray-300 rounded-md" />

        <label htmlFor="tipo" className="block mt-4 text-sm font-medium text-gray-700">
          Tipo
        </label>
        <select name="tipo" id="tipo" value={tipo} onChange={handleTipoChange} className="mt-1 block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md">
          <option>Option 1</option>
          <option>Option 2</option>
          <option>Option 3</option>
          <option>Option 4</option>
          <option>Option 5</option>
          <option>Option 6</option>
          <option>Option 7</option>
          <option>Option 8</option>
        </select>

        <label htmlFor="imagen" className="block mt-4 text-sm font-medium text-gray-700">
          Imagen
        </label>
        <input type="file" name="imagen" id="imagen" accept="image/*" onChange={handleImagenChange} className="mt-1 focus:ring-indigo-500 focus:border-indigo-500 block w-full shadow-sm sm:text-sm border-gray-300 rounded-md" />

        <button type="submit" className="mt-4 bg-indigo-500 text-white px-4 py-2 rounded-md hover:bg-indigo-600 focus:outline-none focus:bg-indigo-600">Guardar</button>
      </form>
    </div>
  );
}
