    import axios from 'axios';
    const response = await axios.get('http://localhost:3001/usuarios');
    const app2 = response.data;
    const apps=app2.forEach((app) => {
      if (app.imagen instanceof Blob) {
        // Crear una nueva instancia de FileReader
        const reader = new FileReader();

        // Configurar el evento load para manejar la finalización de la lectura
        reader.onload = () => {
          // Asignar la URL base64 a la propiedad imagen
          app.imagen = reader.result;
        };

        // Leer el buffer como una URL de datos (base64)
        reader.readAsDataURL(app.imagen);
      }
    });
      export default apps
