    import axios from 'axios';
    const response = await axios.get('http://localhost:3001/usuarios');
    const app2 = response.data;
    const apps=app2.forEach((app) => {
      if (Buffer.isBuffer(app.imagen)) {
        // Convertir el buffer a una cadena de base64
        const base64Image = app.imagen.toString('base64');
        // Crear la URL base64
        const urlImagen = `data:image/png;base64,${base64Image}`;
        // Asignar la URL base64 a la propiedad imagen
        app.imagen = urlImagen;
    }});
      export default apps
