import { useState, useEffect } from 'react';

function parseJwt(token) {
    const base64Url = token.split('.')[1];
    const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    const jsonPayload = decodeURIComponent(window.atob(base64).split('').map(function(c) {
        return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
    }).join(''));

    return JSON.parse(jsonPayload);
}

const useTokenValidation = () => {
    const [isLogged, setIsLogged] = useState(parseJwt(localStorage.getItem('token')).exp * 1000 > Date.now());

    const logout= () => {
        // L贸gica para cerrar sesi贸n: eliminar el token del localStorage y actualizar el estado isLogged a false
        localStorage.removeItem('token');
        setIsLogged(false);
    };

    useEffect(() => {
        const tokenCheckInterval = setInterval(() => {
         isLogged=useState(parseJwt(localStorage.getItem('token')).exp * 1000 > Date.now());
        })
    }, []);

    return { isLogged, logout };
};

export default useTokenValidation;
