import App3 from './Vistas/login';
import Dashboard from './Vistas/Dashboard';
import {ProtectedRoute} from "./components/middelware";
import {ProtectedRouteLogin} from "./components/middelware";
import { BrowserRouter as Router, Route, Routes,Navigate } from 'react-router-dom';
import isLogged from './components/useUser';
import React, { useEffect } from 'react';
function parseJwt (token) {
  const base64Url = token.split('.')[1];
  const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
  const jsonPayload = decodeURIComponent(window.atob(base64).split('').map(function(c) {
      return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
  }).join(''));

  return JSON.parse(jsonPayload);
}

const Main = () => {
  useEffect(() => {
    // Lógica para verificar el estado del token y actualizar isLogged
    const checkTokenExpiration = () => {
      const tokenExpirationTime = parseJwt(localStorage.getItem('token')); // Obtener la hora de expiración del token desde el token almacenado
      if (tokenExpirationTime < Date.now()) {
        isLogged = false; // Si el token ha expirado, actualizar isLogged a false
      }
    };

    checkTokenExpiration();

    // Establecer un temporizador para verificar periódicamente la expiración del token
    const tokenCheckInterval = setInterval(checkTokenExpiration, 60000); // Verificar cada minuto
    return () => clearInterval(tokenCheckInterval); // Limpiar el temporizador al desmontar el componente
  }, []);
  return (
    <Router>
    <Routes>

    <Route path="/home" element={<ProtectedRoute  redirectTo="/"><Dashboard /></ProtectedRoute>} />
    <Route path="/" element={<ProtectedRouteLogin  redirectTo="/Home"><App3 /></ProtectedRouteLogin>} />




      {/* Otras rutas */}
    </Routes>
  </Router>

  );
}

export default Main;
