import '../css/Login.css';
import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
const App3 = () => {
    const[Nombre,setNombre]=useState("");
    const [password, setPassword] = useState('');
    const [loginSuccessful, setLoginSuccessful] = useState(false);
    const [error, setError] = useState('');
    const handdleLogin = (e) =>{
      e.preventDefault();
      const data = {
          Nombre: Nombre,
          password: password
      };
      fetch('http://localhost:3001/login', {
          method: 'POST',
          headers: {
              'Content-Type': 'application/json'
          },
          body: JSON.stringify(data)
      })
      .then(response => response.text()) // Convertir la respuesta a texto
            .then(result => {
                try {
                    const parsedResult = JSON.parse(result);
                    if (parsedResult.token) {
                        localStorage.setItem('token', parsedResult.token);
                        // Redirigir al usuario a la página de inicio
                        window.location.href = '/home';
                    } else {
                        throw new Error(parsedResult.message); // Lanza un error con el mensaje del backend
                    }
                } catch (error) {
                    alert('Ocurrió un error al intentar iniciar sesión'); // Mensaje genérico en caso de respuesta no válida
                }
            })
            .catch(error => {
                alert('Ocurrió un error al intentar iniciar sesión'); // Mensaje genérico en caso de error en la solicitud
            });
    }

// <Header /> onSubmit={handleSubmit}
  return (


<>



  <body class="vista1">
  <div className="container1">
    <div className="card1-body">
      <div className="centrar">

          <form >
            <div className="input-group mb-3">
            <span id="nombre-label" className="input-group-text">Nombre</span>

              <input
                type="text"
                onChange={(event) => {
                  setNombre(event.target.value);
                }}
                className="form-control"
                placeholder="Ingrese su Nombre"
                aria-label="Username"
                aria-describedby="basic-addon1"
              />
            </div>
            <div className="input-group mb-3">
              <span className="input-group-text" id="nombre-label">Contraseña</span>
              <input
                type="password"
                onChange={(event) => {
                  setPassword(event.target.value);
                }}
                className="form-control"
                placeholder="Ingrese su Contraseña"
                aria-label="Username"
                aria-describedby="basic-addon1"
              />
            </div>
            <div className='boton'>
            <button className="btn btn-success"  onClick={handdleLogin}>Login</button>
            </div>
          </form>


      </div>

    </div>
    <h5  className="blue" id="id-company-text">© Azteca Comunicaciones Colombia-2023</h5>

  </div>
  </body>
</>




  );
};


export default App3;
